dashboard-vazio.cy.js
describe('Validação do Dashboard em Estado Vazio', () => {
  beforeEach(() => {
    // Agora o cy.visit('/') vai funcionar por causa da configuração abaixo
    cy.visit('/');
  });

  it('deve exibir os cards, textos e validar os links dos planos', () => {
    cy.get('[data-cy="container-vazio"]').should('be.visible');
    cy.get('[data-cy="texto-principal-vazio"]').should('be.visible');
    cy.get('[data-cy="texto-principal-vazio"]').should('have.text', 'O texto exato que deve aparecer aqui');
    cy.get('[data-cy="link-plano-premium"]').should('have.attr', 'href');
    cy.get('[data-cy="link-plano-premium"]').click();
    cy.url().should('include', '/artigos/plano-premium'); 
  });
});